{
    public partial class AutoMappingConverter : MappingConverter
    {
        public override bool TryGetWrapperType(Type type, out Type wtype)
        {
if (type == typeof()) { wtype = typeof(Wrapper); return true; }
            return base.TryGetWrapperType(type, out wtype);
        }
    }
